mapper.update:
UPDATE SAMPLE
SET ID=#{id}
, NAME=#{name}
,
DESCRIPTION=#{description}
, USE_YN=#{useYn}
WHERE ID=#{id}

mapper.select:
SELECT SEQ, USER_SEQ, URL, FILE_SEQ, VERSION, CTIME, CDATE, UTIME, UDATE, DTIME, DDATE FROM SITE WHERE DDATE =0 AND DTIME=0
AND URL LIKE '%' || #{searchKeyword} || '%'
AND URL LIKE '%' || #{searchKeyword} || '%'
AND USER_SEQ = #{userSeq}
ORDER BY SEQ ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
